import React from "react";
import UserProvider from "../hooks/useUsers";
import { useFavorite } from "../hooks/useFavorite";
import UseFetch from "../hooks/useFetch";
import { API_URL, API_KEY } from "../utils/openWeatherInfo";
import ItemCardFavorite from "../page/itemCardFavorite";

const SelectedPage = () => {
    const { favorite } = useFavorite();
    console.log(favorite[0]);
    const { data, setUrl } = UseFetch();
    // const city = favorite[0];
    return (
        <>
            <UserProvider>
                <h1>SelectedPage</h1>
     (           <ItemCardFavorite
                    dt={data.dt}
                    tempMin={data.main.temp_min}
                    tempMax={data.main.temp_max}
                    name={data.name}
                    country={data.sys.country}
                    icon={data.weather[0].icon}
                    wind={data.wind.speed}
                    data={data}

                />)
                <button
                    type="button"
                    onClick={() => setUrl(`${API_URL}weather?q=${favorite[0]}&appid=${API_KEY}&units=metric`)}
                >
                Посмотреть
                </button>
            </UserProvider>
        </>
    );
};

export default SelectedPage;
