import React, { useState } from "react";
import PropTypes from "prop-types";
import { useAuth } from "../hooks/useAuth";

const ItemCardPage = ({ tempMin, tempMax, main, icon, country, wind }) => {
    const { currentUser } = useAuth();
    const [favorite, setFavorite] = useState([]);
    const date = new Date();
    const handleAddToFavorite = (main) => {
        setFavorite([...favorite, main]);
        console.log(main);
        console.log(favorite);
    };
    return (
        <>
            <div className="container">
                <div className="row">
                    <div className="col-md-4"></div>
                    <div className="col-md-4">
                        <div className="card bg-light">
                            <div className="card-body">
                                <img src={`http://openweathermap.org/img/wn/${icon}@2x.png`} className="card-img-top img-thumbnail" alt="..."></img>
                                <h5 className="card-title">
                                <span class="badge badge-primary">New</span>{main}, {country}</h5>
                                <p>
                                    {date.toLocaleDateString()} - {date.toLocaleTimeString()}
                                </p>
                                <p>Температура от {tempMin} °С до {tempMax} °С</p>
                                <p>Ветер: {wind} м/с</p>
                                {currentUser && (
                                    <button
                                        type="button"
                                        className="btn btn-primary"
                                        onClick={() => handleAddToFavorite(main)}
                                    >
                                        Добавить в избранное
                                    </button>
                                )}
                            </div>
                        </div>
                        <div className="col-md-4"></div>
                    </div>
                </div>
            </div>
        </>
    );
};
ItemCardPage.propTypes = {
    tempMax: PropTypes.number,
    tempMin: PropTypes.number,
    main: PropTypes.string,
    country: PropTypes.string,
    wind: PropTypes.number,
    icon: PropTypes.string,
    handleClick: PropTypes.func,
    city: PropTypes.string
};

export default ItemCardPage;
