import React, { useEffect } from "react";
import UserProvider from "../hooks/useUsers";
import { useFavorite } from "../hooks/useFavorite";
import UseFetch from "../hooks/useFetch";
import { API_URL, API_KEY } from "../utils/openWeatherInfo";
import ItemCardFavorite from "../page/itemCardFavorite";

const SelectedPage = () => {
    const { favorite } = useFavorite();
    const { data, setUrl } = UseFetch();
    // console.log(favorite);
    // useEffect(() => {
    //     favorite.map((item) => {
    //         return (setUrl(`${API_URL}weather?q=${item}&appid=${API_KEY}&units=metric`));
    // });
    // }, []);
    return (
        <>
            <UserProvider>
                {/* <button
                    type="button"
                    className="text-center btn btn-primary"
                    onClick={() => setUrl(`${API_URL}weather?q=${favorite}&appid=${API_KEY}&units=metric`)}
                >
                Посмотреть избранное
                </button> */}
                {/* {data &&
                    favorite.map((data, index) => {
                        return (
                                <ItemCardFavorite
                                    dt={data.dt}
                                    tempMin={data.main.temp_min}
                                    tempMax={data.main.temp_max}
                                    name={data.name}
                                    country={data.sys.country}
                                    icon={data.weather[0].icon}
                                    wind={data.wind.speed}
                                    data={data}
                                />
                        );
                    })
                } */}
                {data &&
                    favorite.map((data, index) => {
                        setUrl(`${API_URL}weather?q=${data}&appid=${API_KEY}&units=metric`)
                        return (
                                <ItemCardFavorite
                                    dt={data.dt}
                                    tempMin={data.main.temp_min}
                                    tempMax={data.main.temp_max}
                                    name={data.name}
                                    country={data.sys.country}
                                    icon={data.weather[0].icon}
                                    wind={data.wind.speed}
                                    data={data}
                                />
                        );
                    })
                }
            </UserProvider>
        </>
    );
};

export default SelectedPage;
