import React from "react";

const ItemCardPage = ({temp_min, temp_max, main, icon, country}) => {
    
    const date = new Date();
    return (
        <>
            <div className="row">
                <div className="col-sm-6">
                    <div className="card">
                    <div className="card-body">
                    <img src={`http://openweathermap.org/img/wn/${icon}@2x.png`} className="card-img-top" alt="..."></img>
                        <h5 className="card-title">{main}, {country}</h5>
                        <p className="card-text">Прогноз погоды</p>
                        <p>
                        {date.toLocaleDateString()} - {date.toLocaleTimeString()}
                        </p>
                        <p>Температура: {temp_min} °С</p>
                        <p>Максимальная температура: {temp_max} °С</p>
                        <a href="#" className="btn btn-secondary">Добавить в избранное</a>
                    </div>
                    </div>
                </div>
            </div>
        </>
    )
}

export default ItemCardPage;
